(160, 1, '2023-04-25 14:21:51', '2023-04-25 11:21:51', '<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Заказать</h2>\n<!-- /wp:heading -->\n\n<!-- wp:contact-form-7/contact-form-selector {\"id\":159,\"title\":\"Форма обратной связи для страницы контактов\"} -->\n<div class=\"wp-block-contact-form-7-contact-form-selector\">[contact-form-7 id=\"159\" title=\"Форма обратной связи для страницы контактов\"]</div>\n<!-- /wp:contact-form-7/contact-form-selector --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Связаться</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Телефон:&nbsp;<strong>(063) 873-26-29</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>E-mail:&nbsp;<a href=\"mailto:studiocake@ukr.net\">studiocake@ukr.net</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://www.facebook.com/studiocake.kiev.ua/timeline\">Мы в Facebook</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://instagram.com/studiocakekiev\">Мы в Instagram</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Адресс:&nbsp;Киев, б-р. Кольцова 14<sup>ж</sup></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Доставка и оплата</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Минимальный вес — 2 кг. Муссовые — от 1 кг.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Стоимость от 600 до 900 грн за 1 кг (в зависимости от декора)</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Доставка по Киеву и области:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>Правый берег — 200 грн</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Левый берег — 300 грн</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>При заказе вносится предоплата 50%</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Способы оплаты:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>Наличными при получении</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Безналичный (предоставляем все документы)</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Перевод на банковскую карту</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>После поступления предоплаты в размере 50% от общей стоимости бронируемая дата. Если же Вы передумали или изменились обстоятельства, предоплата остаётся за нами.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Но мы всегда идём на встречу возникшим обстоятельствам</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->', 'Контакты', '', 'inherit', 'closed', 'closed', '', '31-revision-v1', '', '', '2023-04-25 14:21:51', '2023-04-25 11:21:51', '', 31, 'http://localhost:8888/?p=160', 0, 'revision', '', 0),
